(function () {
    var userType = "returning";
    var expectedType = "WEB";
    var expectedStatus = "CLOSED";
    var isReturning = false;

    if (guest && guest.sessions && guest.sessions.length > 0) {
        for (var index = 0; index < guest.sessions.length; index++) {
            if (
                guest.sessions[index] &&
                expectedType === guest.sessions[index].sessionType &&
                expectedStatus === guest.sessions[index].status
            ) {
                isReturning = true;
                break;
            }
        }
    }

    if (isReturning && userType === "returning") {
        return true;
    } else if (!isReturning && userType === "new") {
        return true;
    } else {
        return false;
    }
})() && (function () {
    var visited = "has";
    var pageName = "/snowboarding";
    var pageValues = pageName.split(',');
    var expectedType = "WEB";
    var expectedStatus = "OPEN";
    var pageMatch = false;

    if (guest && guest.sessions && guest.sessions.length > 0) {
        loop:
        for (var i = 0; i < guest.sessions.length; i++) {
            if (guest.sessions[i]) {
                if (
                    guest.sessions[i].sessionType !== expectedType
                    || guest.sessions[i].status !== expectedStatus
                ) {
                    continue loop;
                } else if (
                    guest.sessions[i].events
                ) {
                    for (var j = 0; j < guest.sessions[i].events.length; j++) {
                        if (guest.sessions[i].events[j].arbitraryData) {
                            if (guest.sessions[i].events[j].arbitraryData.page) {
                                var viewedpage = guest.sessions[i].events[j].arbitraryData.page;
                                for (var k = 0; k < pageValues.length; k++) {   
                                    if (viewedpage === pageValues[k]) { 
                                        pageMatch = true;   
                                        break loop; 
                                    }   
                                }
                            }
                        }
                    }
                }
            }
        }
    }

    return (visited === "has") && pageMatch || (visited === "has not") && !pageMatch;
})();
